// CÃ“DIGO PARA EL NODO "Codigo ETHEREUM" (AnÃ¡lisis completo mejorado)
// Obtener entradas de noticias y precios
const newsInput = $input.all().find(item => item.json.articles);
const response = newsInput?.json || {};
const articles = response.articles || [];
const pricesInput = $input.all().find(item => Array.isArray(item.json.prices));
const prices = pricesInput?.json?.prices || [];
const patternsInput = $input.all().find(item => item.json.patterns);
const patterns = patternsInput?.json?.patterns || [];
const technicalMetrics = patternsInput?.json?.technicalMetrics || {};

if (prices.length < 2) {
    return [{ json: { error: 'No se encontraron precios suficientes para ETH' } }];
}

// ConfiguraciÃ³n
const asset = 'ETH';
const now = new Date();
const lookbackPeriod = 24 * 60 * 60 * 1000; // 24 horas
const windowStart = new Date(now.getTime() - lookbackPeriod);

// CÃ¡lculo de precio y variaciÃ³n en 24h
const currentPrice = prices[prices.length - 1][1];
const price24hAgo = prices[0][1];
const priceMovement = currentPrice - price24hAgo;
const movementPercentage = (priceMovement / price24hAgo) * 100;

// AnÃ¡lisis de momentum mejorado
const priceData = prices.map(p => p[1]);
const recentPrices = priceData.slice(-12); // Ãšltimas 12 horas
const momentum12h = ((recentPrices[recentPrices.length - 1] - recentPrices[0]) / recentPrices[0]) * 100;

// Palabras clave para anÃ¡lisis de sentimiento ampliado
const positiveWords = ['gain', 'rise', 'bull', 'profit', 'surge', 'growth', 'strong', 'up', 'bullish', 'rally', 'pump', 'moon', 'breakthrough', 'adoption', 'institutional', 'upgrade', 'launch'];
const negativeWords = ['loss', 'fall', 'bear', 'drop', 'weak', 'down', 'decline', 'crash', 'bearish', 'dump', 'sell-off', 'correction', 'regulatory', 'hack', 'concern', 'delay'];

let sentimentScore = 0;
let recentNewsCount = 0;
let newsImpact = 0;

// Evaluar sentimiento en noticias recientes relacionadas con ETH
articles.forEach(article => {
    const publishedDate = new Date(article.publishedAt);
    const text = (article.title + ' ' + (article.description || '')).toLowerCase();

    // Solo analizar si mencionan Ethereum o ETH
    if (publishedDate > windowStart && (text.includes('ethereum') || text.includes(' eth ') || text.includes('vitalik'))) {
        recentNewsCount++;
        const cleanedText = text.replace(/[^a-z\s]/g, '');

        let articleScore = 0;
        positiveWords.forEach(word => {
            const matches = (cleanedText.match(new RegExp(word, 'g')) || []).length;
            articleScore += matches;
        });

        negativeWords.forEach(word => {
            const matches = (cleanedText.match(new RegExp(word, 'g')) || []).length;
            articleScore -= matches;
        });

        // Peso por recencia (noticias mÃ¡s recientes tienen mÃ¡s peso)
        const hoursAgo = (now - publishedDate) / (1000 * 60 * 60);
        const recencyWeight = Math.max(0.1, 1 - (hoursAgo / 24));

        sentimentScore += articleScore * recencyWeight;
        newsImpact += Math.abs(articleScore) * recencyWeight;
    }
});

// AnÃ¡lisis de patrones tÃ©cnicos para seÃ±ales
let patternSignal = 0;
let patternConfidence = 0;

patterns.forEach(pattern => {
    const patternLower = pattern.toLowerCase();

    // Patrones alcistas
    if (patternLower.includes('alcista') || patternLower.includes('suelo') || patternLower.includes('rebote') || patternLower.includes('sobrevendido')) {
        patternSignal += 1;
        patternConfidence += 0.2;
    }

    // Patrones bajistas
    if (patternLower.includes('bajista') || patternLower.includes('techo') || patternLower.includes('correcciÃ³n') || patternLower.includes('sobrecomprado') || patternLower.includes('resistencia')) {
        patternSignal -= 1;
        patternConfidence += 0.2;
    }

    // Patrones de consolidaciÃ³n
    if (patternLower.includes('lateral') || patternLower.includes('consolidaciÃ³n')) {
        patternConfidence += 0.1;
    }

    // Alta volatilidad aumenta incertidumbre
    if (patternLower.includes('volatilidad')) {
        patternConfidence -= 0.1;
    }
});

// LÃ³gica de decisiÃ³n mejorada
let signal = 'Mantener posiciÃ³n';
let confidence = 'Baja';
let prediction24h = 0;
let prediction12h = 0;

// Calcular predicciones basadas en mÃºltiples factores
const sentimentWeight = 0.3;
const patternWeight = 0.4;
const momentumWeight = 0.3;

const combinedSignal = (sentimentScore * sentimentWeight) + (patternSignal * patternWeight) + (momentum12h * momentumWeight);

// PredicciÃ³n para 24 horas
prediction24h = combinedSignal * 0.8; // Factor de atenuaciÃ³n

// PredicciÃ³n para 12 horas (mÃ¡s conservadora)
prediction12h = combinedSignal * 0.4;

// Ajustar seÃ±ales de trading
if (combinedSignal > 2 && movementPercentage > 1 && patternConfidence > 0.3) {
    signal = `COMPRAR ${asset}`;
    confidence = 'Alta';
} else if (combinedSignal > 1 && movementPercentage > 0) {
    signal = `Considerar COMPRA de ${asset}`;
    confidence = 'Media';
} else if (combinedSignal < -2 || (movementPercentage < -3 && patternSignal < 0)) {
    signal = `EVITAR comprar ${asset}`;
    confidence = 'Alta';
} else if (combinedSignal < -1) {
    signal = `Considerar VENTA de ${asset}`;
    confidence = 'Media';
}

// Ajustar confianza basada en volumen de datos
if (recentNewsCount < 3 && patterns.length < 2) {
    confidence = 'Baja';
}

// Retorno final con mÃ©tricas expandidas
return [{
    json: {
        signal: `${signal} (Confianza: ${confidence})`,
        analysis: `Precio actual: $${currentPrice.toFixed(2)} | Cambio 24h: ${movementPercentage.toFixed(2)}% | Momentum 12h: ${momentum12h.toFixed(2)}% | Noticias: ${recentNewsCount} | Patrones: ${patterns.length}`,
        predictions: {
            movement24h: `${prediction24h > 0 ? '+' : ''}${prediction24h.toFixed(2)}%`,
            movement12h: `${prediction12h > 0 ? '+' : ''}${prediction12h.toFixed(2)}%`
        },
        timestamp: now.toISOString(),
        asset,
        currentPrice: currentPrice.toFixed(2),
        movement24h: movementPercentage.toFixed(2),
        momentum12h: momentum12h.toFixed(2),
        sentimentScore: sentimentScore.toFixed(1),
        patternSignal: patternSignal,
        patternConfidence: patternConfidence.toFixed(2),
        recentNews: recentNewsCount,
        patterns: patterns,
        technicalMetrics: technicalMetrics,
        combinedSignal: combinedSignal.toFixed(2),
        newsImpact: newsImpact.toFixed(1)
    }
}];

// CÃ“DIGO PARA EL NODO "Code" (AnÃ¡lisis de patrones)
const prices = $input.item.json.prices || [];

function detectPatterns(prices) {
    const patterns = [];
    const priceData = prices.map(p => p[1]); // Solo los precios
    const timeData = prices.map(p => p[0]); // Solo los timestamps

    // AnÃ¡lisis de tendencias mÃ¡s robusto
    function calculateMovingAverage(data, period) {
        const result = [];
        for (let i = period - 1; i < data.length; i++) {
            const sum = data.slice(i - period + 1, i + 1).reduce((a, b) => a + b, 0);
            result.push(sum / period);
        }
        return result;
    }

    // Calcular RSI simplificado
    function calculateRSI(prices, period = 14) {
        if (prices.length < period + 1) return 50; // Valor neutro si no hay suficientes datos

        let gains = 0, losses = 0;
        for (let i = 1; i <= period; i++) {
            const change = prices[prices.length - i] - prices[prices.length - i - 1];
            if (change > 0) gains += change;
            else losses += Math.abs(change);
        }

        const avgGain = gains / period;
        const avgLoss = losses / period;
        const rs = avgGain / avgLoss;
        return 100 - (100 / (1 + rs));
    }

    // Detectar soportes y resistencias
    function findSupportResistance(prices) {
        const highs = [], lows = [];
        for (let i = 1; i < prices.length - 1; i++) {
            if (prices[i] > prices[i-1] && prices[i] > prices[i+1]) {
                highs.push(prices[i]);
            }
            if (prices[i] < prices[i-1] && prices[i] < prices[i+1]) {
                lows.push(prices[i]);
            }
        }
        return { resistance: Math.max(...highs), support: Math.min(...lows) };
    }

    if (priceData.length < 10) {
        patterns.push("Datos insuficientes");
        return patterns;
    }

    const currentPrice = priceData[priceData.length - 1];
    const ma5 = calculateMovingAverage(priceData, 5);
    const ma20 = calculateMovingAverage(priceData, Math.min(20, priceData.length - 1));
    const rsi = calculateRSI(priceData);
    const supportResistance = findSupportResistance(priceData);

    // Patrones de velas mÃ¡s precisos
    for (let i = 2; i < priceData.length - 1; i++) {
        const prev2 = priceData[i - 2];
        const prev1 = priceData[i - 1];
        const current = priceData[i];
        const next = priceData[i + 1];

        // Doble techo
        if (prev1 > prev2 && prev1 > current && Math.abs(prev1 - current) / prev1 > 0.02) {
            patterns.push("Doble techo - SeÃ±al bajista");
        }

        // Doble suelo
        if (prev1 < prev2 && prev1 < current && Math.abs(current - prev1) / prev1 > 0.02) {
            patterns.push("Doble suelo - SeÃ±al alcista");
        }

        // Martillo
        const bodySize = Math.abs(current - prev1) / prev1;
        if (bodySize < 0.01 && current > prev1 * 0.98) {
            patterns.push("Martillo - Potencial reversiÃ³n alcista");
        }
    }

    // AnÃ¡lisis de tendencias con MA
    if (ma5.length > 0 && ma20.length > 0) {
        const ma5Current = ma5[ma5.length - 1];
        const ma20Current = ma20[ma20.length - 1];

        if (ma5Current > ma20Current && currentPrice > ma5Current) {
            patterns.push("Tendencia alcista fuerte");
        } else if (ma5Current < ma20Current && currentPrice < ma5Current) {
            patterns.push("Tendencia bajista fuerte");
        } else {
            patterns.push("Tendencia lateral/consolidaciÃ³n");
        }
    }

    // AnÃ¡lisis RSI
    if (rsi > 70) {
        patterns.push("RSI sobrecomprado - Posible correcciÃ³n");
    } else if (rsi < 30) {
        patterns.push("RSI sobrevendido - Posible rebote");
    }

    // AnÃ¡lisis de soporte/resistencia
    const distanceToResistance = (supportResistance.resistance - currentPrice) / currentPrice;
    const distanceToSupport = (currentPrice - supportResistance.support) / currentPrice;

    if (distanceToResistance < 0.02) {
        patterns.push("Cerca de resistencia - PresiÃ³n vendedora");
    }
    if (distanceToSupport < 0.02) {
        patterns.push("Cerca de soporte - Posible rebote");
    }

    // Volatilidad
    const recentPrices = priceData.slice(-10);
    const volatility = Math.sqrt(recentPrices.reduce((sum, price, i) => {
        if (i === 0) return 0;
        const change = (price - recentPrices[i-1]) / recentPrices[i-1];
        return sum + change * change;
    }, 0) / (recentPrices.length - 1));

    if (volatility > 0.05) {
        patterns.push("Alta volatilidad - Mayor riesgo");
    } else if (volatility < 0.01) {
        patterns.push("Baja volatilidad - Posible ruptura prÃ³xima");
    }

    return [...new Set(patterns)];
}

const detectedPatterns = detectPatterns(prices);

// Calcular mÃ©tricas adicionales
const priceData = prices.map(p => p[1]);
const currentPrice = priceData[priceData.length - 1];
const price24hAgo = priceData[0];
const priceChange24h = ((currentPrice - price24hAgo) / price24hAgo) * 100;

// Calcular volatilidad reciente
const recentPrices = priceData.slice(-24); // Ãšltimas 24 horas
const avgPrice = recentPrices.reduce((a, b) => a + b, 0) / recentPrices.length;
const variance = recentPrices.reduce((sum, price) => sum + Math.pow(price - avgPrice, 2), 0) / recentPrices.length;
const volatility = Math.sqrt(variance) / avgPrice;

return [{
    json: {
        ...$input.item.json,
        patterns: detectedPatterns,
        technicalMetrics: {
            currentPrice: currentPrice.toFixed(2),
            priceChange24h: priceChange24h.toFixed(2),
            volatility: (volatility * 100).toFixed(2),
            dataPoints: prices.length
        }
    }
}];
